import pdb
import tushare as ts

# def trading_date


def stock_return(api, ticker, buy_date, holding_period=10):
    df_cal = api.trade_cal(start_date=buy_date, is_open=1)
    trading_dates = df_cal['cal_date'].tolist()
    assert trading_dates[0] == buy_date
    sell_date = trading_dates[holding_period]
    adj_factor_on_buy = pro.adj_factor(
        ts_code=ticker, trade_date=buy_date)['adj_factor'].tolist()[0]
    adj_factor_on_sell = pro.adj_factor(
        ts_code=ticker, trade_date=sell_date)['adj_factor'].tolist()[0]
    df_price = api.daily(
        ts_code=ticker, start_date=buy_date, end_date=sell_date)
    close_price = df_price['close'].tolist()
    close_buy = close_price[-1]
    close_sell = close_price[0]
    return (close_sell * adj_factor_on_sell) / (
        close_buy * adj_factor_on_buy) - 1


